extension.code:
typedef struct _op_item{
	time_t op_mtime;
	zend_op_array op_codes;
}op_item;

#include "SlightPHP_globals.h"
#include "SlightPHP_globals.c"

function.code:
	if (Z_TYPE_P(_this_zval) == IS_OBJECT) {
		zval *data = zend_read_property(_this_ce,_this_zval,"appDir",sizeof("appDir")-1,1 TSRMLS_CC);
    		RETURN_ZVAL(data,1,0);
	}
	return NULL;


	zval *_debug_flag = zend_read_property(_this_ce,_this_zval,"_debug",sizeof("_debug")-1,1 TSRMLS_CC);
	int ret = SlightPHP_loadFile(filepath,_debug_flag TSRMLS_CC);
	ZVAL_LONG(return_value,ret);


	zval *pluginsDir = zend_read_property(_this_ce,_this_zval,"pluginsDir",sizeof("pluginsDir")-1,1 TSRMLS_CC);
	zval *_debug_flag = zend_read_property(_this_ce,_this_zval,"_debug",sizeof("_debug")-1,1 TSRMLS_CC);
	char*inc_filename;
	int ret;
	spprintf(&inc_filename,0,"%s%c%s.class.php",Z_STRVAL_P(pluginsDir),DEFAULT_SLASH,Z_STRVAL_P(pluginName));
	zval file_name;
	ZVAL_STRING(&file_name,inc_filename,1);
	ret = SlightPHP_loadFile(&file_name,_debug_flag TSRMLS_CC);
	efree(inc_filename);
	ZVAL_LONG(return_value,ret);

	
	
	zval *zone=NULL;
	zval *class_name=NULL;
	zval *method=NULL;

	zval **array;
	zval **token;
	zval *path=NULL;

	//{{{
	zval *_debug_flag = zend_read_property(_this_ce,_this_zval,"_debug",sizeof("_debug")-1,1 TSRMLS_CC);
	//convert_to_long(_debug_flag);
	//_debug = Z_LVAL_P(_debug_flag);
	//}}}

	zend_is_auto_global("_SERVER", sizeof("_SERVER") - 1 TSRMLS_CC);
	if (zend_hash_find(&EG(symbol_table), "_SERVER",sizeof("_SERVER"), (void **) &array) == SUCCESS &&
			Z_TYPE_PP(array) == IS_ARRAY &&
            zend_hash_find(Z_ARRVAL_PP(array), "PATH_INFO", sizeof("PATH_INFO"), (void **) &token) == SUCCESS
	){
		path = token[0];
    	}

	if (path){
		zval delim;
    		ZVAL_STRING(&delim, "/", 0);
		zval *tmp_array;
		MAKE_STD_ZVAL(tmp_array);
		array_init(tmp_array);
		php_explode(&delim, path, tmp_array, -1);
		int n_elems = zend_hash_num_elements(Z_ARRVAL_P(tmp_array));
		zval **args = NULL;
		if(zend_hash_index_exists(Z_ARRVAL_P(tmp_array),1)){
			//zone
			if(zend_hash_index_find(Z_ARRVAL_P(tmp_array), 1, (void **)&args) != FAILURE) {
				if(Z_STRLEN_PP(args)>0){
					zone = args[0];
				}
			}
		}
		if(zend_hash_index_exists(Z_ARRVAL_P(tmp_array),2)){
			//zone
			if(zend_hash_index_find(Z_ARRVAL_P(tmp_array), 2, (void **)&args) != FAILURE) {
				if(Z_STRLEN_PP(args)>0){
					class_name = args[0];
				}
			}
		}
		if(zend_hash_index_exists(Z_ARRVAL_P(tmp_array),3)){
			//zone
			if(zend_hash_index_find(Z_ARRVAL_P(tmp_array), 3, (void **)&args) != FAILURE) {
				if(Z_STRLEN_PP(args)>0){
					method = args[0];
				}
			}
		}
		FREE_ZVAL(tmp_array);
			
	}
	if(!zone){
		zone = zend_read_property(_this_ce,_this_zval,"defaultZone",sizeof("defaultZone")-1,1 TSRMLS_CC);
		//MAKE_STD_ZVAL(zone);
		//ZVAL_STRING(zone, "index", 0);
	}
	if(!class_name){
		class_name = zend_read_property(_this_ce,_this_zval,"defaultClass",sizeof("defaultClass")-1,1 TSRMLS_CC);
		//MAKE_STD_ZVAL(class_name);
		//ZVAL_STRING(class_name, "default", 0);
	}
	if(!method){
		method = zend_read_property(_this_ce,_this_zval,"defaultMethod",sizeof("defaultMethod")-1,1 TSRMLS_CC);
		//MAKE_STD_ZVAL(method);
		//ZVAL_STRING(method, "entry", 0);
	}

	zval *tmp_result=NULL;
	MAKE_STD_ZVAL(tmp_result);
	ZVAL_STRING(tmp_result, "", 0);

	zval *appDir = zend_read_property(_this_ce,_this_zval,"appDir",sizeof("appDir")-1,1 TSRMLS_CC);

	if(SlightPHP_load(appDir,zone,class_name,_debug_flag TSRMLS_CC) == SUCCESS){
		if(SlightPHP_run(zone,class_name,method,&tmp_result,_debug_flag TSRMLS_CC)==SUCCESS){
			*return_value = *tmp_result;
			zval_copy_ctor(return_value);
			FREE_ZVAL(tmp_result);
		}else{
			RETURN_FALSE;
		};

	}else{
		RETURN_FALSE;
	}
	//FREE_ZVAL(zone);
	//FREE_ZVAL(method);
	//FREE_ZVAL(class_name);
